      styleimg += `background-color:${backgroundColor} !important;`
  }

  aspectMode = (!aspectMode || aspectMode === '') ? 'fit' : aspectMode
  size = (!size || size === '') ? 'md' : size
  aspectMode = upperalldigit(aspectMode)
  if (size.indexOf("px") >= 0) {
      style2 += `width:${size};`
