  } else {
      size = 'Ex' + upperalldigit(size)
  }

  let ratio;
  if (!aspectRatio || aspectRatio === '') {
      ratio = '100'
  } else {
      ratio = aspectRatio.split(':')
      ratio = ratio[1] * 100 / ratio[0]
  }
  let fl = ''
  if (flex > 3) {
      style += `-webkit-box-flex:${flex};flex-grow:${flex};`
  } else {
      fl = (flex >= 0) ? `fl${flex}` : ''
  }
